import React, { Component } from 'react'



export default class Card extends Component {
    state = {
        words: [
            {
                id: "1", name: "For", meaning: "İçin", examples1: "He wants the best for me", examples2 : "Thanks for waiting for me."
            },
            {
                id: "2", name: "Dream", meaning: "Hayal, Rüya", examples1: "my dream is not his dream."
            },
            {
                id: "3", name: "Situation", meaning: "Durum", examples1: "The situation is not easy."
            },
            {
                id: "4", name: "Say", meaning: "Söylemek, Demek", examples1: "My father says, “Travelling is dangerous.”"
            },
            {
                id: "5", name: "Comfortable", meaning: "Rahat", examples1: "Travelling is not comfortable."
            },
            {
                id: "6", name: "To", meaning: "(mek-mak)", examples1: "He wants to speak with me"
            },
            {
                id: "7", name: "Die", meaning: "ölmek", examples1: "İf you talk you can die."
            }
        ]
    }


    

    render() {

        return (

            this.state.words.map(word => {
                return (
                    <div className="col-lg-4 my-2">
                        <div className="card">
                            <button className="btn btn-outline-primary btn-lg" data-bs-toggle="collapse"  href="#a" aria-expanded="false" aria-controls="a">{word.name}</button> 
                            <div className="card-body collapse" id="a">
                                <h5 className="card-title card-title-style">Anlamı = {word.meaning} </h5>
                                <h6 className='card-text card-example-style'> Örnekler</h6>
                                <p className="card-text card-text-style"> 1.{word.examples1} </p>
                                <p className="card-text card-text-style"> 2.{word.examples2} </p>
                                <p className="card-text card-text-style"> 3.{word.examples3} </p>
                                <p className="card-text card-text-style"> 4.{word.examples4} </p>
                                <p className="card-text card-text-style"> 5.{word.examples5} </p>
                                
                            </div>
                            
                        </div>
                    </div>

                );

            })



        )
    }
}
