import React, { Component } from 'react'

export default class Card extends Component {
    



    render() {
        return (
            this.state.words.map(word => {
                return (
                    <div className="col-lg-4 my-2">
                        <div className="card bg-dark">
                            <button className="btn btn-outline-primary  btn-lg " data-bs-toggle="collapse"  href={"#".concat(word.name)} aria-expanded="false" >{word.name}</button> 
                            <div className="card-body collapse" id={word.name}>
                                <h5 className="card-title card-title-style"> {word.meaning} </h5>
                                <h6 className='card-text card-example-style text-center mt-4'> Ã–rnekler</h6>

                                <div className='card-footer'>
                                    <p className="card-text card-text-style">1. {word.examples1} </p>
                                    <p className="card-text card-text-style">2. {word.examples2} </p>
                                    <p className="card-text card-text-style">3. {word.examples3} </p>
                                </div>
                        
                            </div>
                        </div>
                    </div>
                );
            })
        )
    }
}
