import React, { Component } from 'react'



export default class Card extends Component {
    state = {
        words :[
            {
                id: 1, name: "For", meaning: "İçin", examples: "He wants the best for me"
            },
            {
                id: 2, name: "Dream", meaning: "Hayal, Rüya", examples: "my dream is not his dream."
            },
            {
                id: 3, name: "Situation", meaning: "Durum", examples: "The situation is not easy."
            },
            {
                id: 4, name: "Say", meaning: "Söylemek, Demek", examples: "My father says, “Travelling is dangerous.”"
            },
            {
                id: 5, name: "Comfortable", meaning: "Rahat", examples: "Travelling is not comfortable."
            },
            {
                id: 6, name: "To", meaning: "(mek-mak)", examples: "He wants to speak with me"
            },
            {
                id: 7, name: "Die", meaning: "ölmek", examples: "İf you talk you can die."
            }
        ]
    }


    render() {

        return (


            <div className="col-lg-4 my-2">
                <div className="card">
                    <div className="card-header">
                        <button className="btn btn-outline-primary btn-lg text-bold">{this.props.word}</button>
                    </div>
                    <div className="card-body">
                        <h5 className="card-title"> Anlamı </h5>
                        <p className="card-text"> Örnekler </p>
                        <p className="card-text"> Örnekler </p>
                        <p className="card-text"> Örnekler </p>
                    </div>
                </div>
            </div>

        )
    }
}
